import React, { useState } from "react";
import {
  Card,
  CardBody,
  CardTitle,
  CardText,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
} from "reactstrap";

const Contact = () => {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    message: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  let mailto = null;

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form data submitted:", formData);
    const subject = encodeURIComponent("New Form Submission");
    const body = encodeURIComponent(JSON.stringify(formData, null, 2));
    mailto = `mailto:recipient-email@example.com?subject=${subject}&body=${body}`;
  };
  return (
    <Card color="dark" inverse className="primary-card">
      <CardBody>
        <CardTitle tag="h5">Contact Me</CardTitle>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label for="firstName">First Name</Label>
            <Input
              type="text"
              name="firstName"
              id="firstName"
              placeholder="Enter your first name"
              value={formData.firstName}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <Label for="lastName">Last Name</Label>
            <Input
              type="text"
              name="lastName"
              id="lastName"
              placeholder="Enter your last name"
              value={formData.lastName}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <Label for="email">Email</Label>
            <Input
              type="email"
              name="email"
              id="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <Label for="message">Message</Label>
            <Input
              type="textarea"
              name="message"
              id="message"
              placeholder="Enter your message"
              value={formData.message}
              onChange={handleChange}
            />
          </FormGroup>
          <Button color="primary" type="submit">
            Submit
          </Button>
        </Form>
        <CardText>{mailto}</CardText>
      </CardBody>
    </Card>
  );
};

export default Contact;
